sources.styles/utils/mixin.scss:
@mixin onTablet {
  @media (min-width: $tablet-min-width) {
    @content;
  }
}

@mixin onDesktop {
  @media (min-width: $desktop-min-width) {
    @content;
  }
}


@mixin contentPaddingInLine() {
  padding-inline: 20px;

  @include onTablet {
    padding-inline: 72px;
  }

  @include onDesktop() {
    max-width: 1260px;
    margin-inline: auto;
    padding-inline: 120px;
  }
}

.container {
  @include contentPaddingInLine;
}

@mixin hover($property, $toValue) {
  transition: #{$property} 0.3s;
  &:hover {
    #{$property}: $toValue;
  }
}

@mixin pageGrid {
  --columns: 2;
  display: grid;
  column-gap: 20px;
  grid-template-columns: repeat(var(--columns), 1fr);

  @include onTablet {
    --columns: 6;
    column-gap: 24px;
  }

  @include onDesktop {
    --columns: 12;
  }
}

@mixin hoverActiveEffects() {
  &:hover {
      background-color: $buttom-color;
      color: $main-text-color;
    }

    &:active {
      background-color: $pressed-bottom-color;
      color: $buttom-color;
    }
}

sources.styles/blockes/page.scss:
* {
  box-sizing: border-box;
}

.page {
  font-family: "Inter", sans-serif;
  font-size: 16px;
  line-height: 140%;
  color: $main-text-color;

  scroll-behavior: smooth;

  ::-webkit-scrollbar-track {
    background: #f1f1f1;
  }

  &:has(.page__menu:target) {
    overflow: hidden;
  }

  &__body {
    margin: 0;
    min-width: 320px;
  }

  &__menu {
    position: fixed;
    top: 0;
    left: 0;
    right: 0;

    transition: all 0.3s;
    opacity: 0;
    transform: translateX(-100%);
    pointer-events: none;

    &:target {
      opacity: 1;
      transform: translateX(0);
      pointer-events: all;
    }
  }
}

sources.styles/blockes/header.scss:
.header {
  background-image: url(../images/img-mob-webp.webp);
  background-size: cover;

  @include onTablet {
    background-image: url(../images/img-tab-webp.webp);
  }

  @include onDesktop {
    background-image: url(../images/img-web-webp.webp);
  }

  &__content {
    height: 100vh;

    display: flex;
    flex-direction: column;
    justify-content: space-between;
  }

  &__waves {
    height: 57px;
    width: 48px;
    margin-bottom: 16px;
    background-image: url(../images/icon-waves.svg);
    background-size: cover;

    @include onTablet {
      widows: 64px;
      height: 76px;
      margin-bottom: 40px;
    }
  }

  &__title {
    margin: 0;
    font-style: italic;
    font-weight: 800;
    font-size: 32px;
    line-height: 100%;
    letter-spacing: -1.5px;

    @include onTablet {
      font-size: 40px;
    }
  }

  &__bottom {
    padding-bottom: 40px;

    @include onTablet {
      padding-bottom: 64px;
    }
  }
}

sources.styles/blockes/top-bar.scss:
.top-bar {
  padding: 24px;

  display: flex;
  justify-content: space-between;
  align-items: center;

  @include onTablet {
    padding-block: 32px;
  }

  &__icons {
    display: flex;
    gap: 24px;
  }

  &__logo-link {
    display: flex;
  }

  &__logo {
    height: 16px;

    @include hover(transform, scale(1.2));

    @include onTablet {
      height: 24px;
    }
  }
}

sources.styles/blockes/icon.scss:
.icon {
  display: block;
  height: 24px;
  width: 24px;
  background-size: cover;
  background-position: center;

  @include hover(transform, scale(1.2));

  @include onTablet {
    height: 32px;
    width: 32px;
  }

  &--phone {
    position: relative;
    background-image: url(../images/phone.svg);

    &::after {
      content: "+1 234 555-55-55";
      position: absolute;
      display: block;
      right: 8px;
      bottom: 8px;

      width: 180px;
      color: $main-text-color;

      font-size: 12px;
      font-weight: 700;
      line-height: 16px;
      letter-spacing: 2px;

      transition: transform 0.3s;
      transform: scale(0);
      transform-origin: left;
    }

    &:hover::after {
      transform: scale(1);
    }
  }

  &--menu {
    background-image: url(../images/icon-burger-menu.svg);

    &:hover {
      background-image: url(../images/icon-burger-menu-hover.svg);
    }
  }

  &--close {
    background-image: url(../images/icon-close.svg);
  }
}

sources.styles/blockes/menu.scss:
.menu {
  box-sizing: border-box;

  height: 100vh;
  background-color: $back-menu;
  font-weight: 700;
  text-transform: uppercase;

  &__top {
    margin-bottom: 32px;

    @include onTablet {
      margin-bottom: 0;
    }
  }

  &__bottom {
    margin-top: 32px;

     @include onTablet {
      margin-top: 56px;
    }
  }

  &__nav {
    margin-bottom: 40px;
  }

  &__phone__number {
    display: block;
    margin-bottom: 16px;

    font-size: 16px;
    line-height: 27px;
    color: inherit;
    text-decoration: none;
  }

  &__call-to-order {
    display: block;
    width: fit-content;

    padding-bottom: 7px;
    border-bottom: 1px solid $main-text-color;

    font-size: 12px;
    line-height: 16px;
    letter-spacing: 2px;
    color: inherit;
    text-decoration: none;
  }
}

sources.styles/blockes/nav.scss:
.nav {
  &__list {
    margin: 0;
    padding: 0;
    list-style: none;

    display: flex;
    flex-direction: column;
    gap: 32px;
  }

  &__link {
    position: relative;
    color: inherit;
    text-decoration: none;
    font-size: 22px;
    line-height: 22px;
    letter-spacing: 2px;

    &::after {
      content: '';
      position: absolute;
      bottom: -8px;
      display: block;
      height: 1px;
      width: 100%;
      background-color: $main-text-color;

      transition: transform 0.3s;
      transform: scale(0);
      transform-origin: left;
    }

    &:hover::after {
      transform: scale(1);
    }
  }
}

