sources.styles/blockes/recommended.scss:
.recommended {
  &__products {
    @include pageGrid;
    row-gap: 40px;
  }

  &__product {
    grid-column: 1 / -1;

    @include onTablet {
      grid-column: 2 / -2;
    }

    @include onDesktop {
      grid-column: span 4;
    }
  }
}

sources.styles/blockes/how-to-buy.scss:
.how-to-buy {
  @include pageGrid;

  &__title {
    grid-column: 1 / -1;

    @include onTablet {
      grid-column: 1 / 4;
    }

    @include onDesktop {
      grid-column: span 5;
    }
  }

  &__content {
    grid-column: 1 / -1;

    @include onTablet {
      grid-column: 4 / 7;
    }

    @include onDesktop {
      grid-column: span 5;
    }
  }

  &__benefits {
    display: grid;
    row-gap: 16px;

    margin: 0;
    margin-bottom: 40px;
    padding: 0;
    list-style: none;
    font-weight: bold;
  }

  &__description {
    margin: 0;
    line-height: 27px;
  }
}

sources.styles/blockes/categories.scss:
.categories {
  &__wrapper {
    display: grid;
    row-gap: 40px;

    @include onTablet {
      row-gap: 64px;
    }
  }
}

.category {
  &__photos {
    @include pageGrid;
    margin-bottom: 16px;
  }

  &__link {
    position: relative;
    text-decoration: none;

    &--wide {
      @include onTablet {
        grid-column: span 4;
      }

      @include onDesktop {
        grid-column: span 8;
      }
    }

    &--square {
      height: 0;
      padding-bottom: 100%;

      @include onTablet {
        grid-column: span 2;
      }

      @include onDesktop {
        grid-column: span 4;
      }
    }
  }

  &__photo {
    width: 100%;
    height: 324px;
    object-fit: cover;

    @include hover(transform, scale(1.05));
  }

  &__title {
    margin: 0;
    font-weight: 700;
    font-size: 24px;
    line-height: 140%;
  }

  &__text {
    font-size: 24px;
    font-weight: 700;
    line-height: 140%;
    color: $main-text-color;
  }
}

sources.styles/blockes/contacts.scss:
.contacts {
  &__form {
    display: flex;
    width: 100%;
    max-width: 412px;
    flex-direction: column;
    gap: 16px;
  }

  &__main {
    @include onTablet {
      display: flex;
      gap: 24px;
      justify-content: space-between;
    }

    @include onDesktop {
      gap: 198px;
    }
  }

  &__field {
    display: flex;
    width: 100%;
    height: 48px;

    padding: 14px 0 14px 16px;

    align-items: flex-start;
    border-radius: 40px;
    color: $contact-text-color;
    background: $contact-border-color;
    outline: none;

    border: $contact-border-color;

    &:-webkit-autofill,
    &:-webkit-autofill:hover,
    &:-webkit-autofill:focus {
      -webkit-box-shadow: 0 0 0px 1000px $contact-border-color inset;
    }

    &:hover {
      border: 1px solid $border-color;
      background: $contact-border-color;
      color: $main-text-color;
    }

    &:active {
      border: 1px solid $main-text-color;
      background: $contact-border-color;
      color: $main-text-color;
    }

    &::placeholder {
      color: $contact-text-color;
      font-family: Inter;
      font-size: 14px;
    }
  }

  &__field--message {
    display: flex;
    width: 100%;
    max-width: 412px;
    height: 200px;
    font-family: Inter;

    align-items: flex-start;
    justify-content: left;
    gap: 10px;

    border-radius: 16px;
    resize: none;

    ::-webkit-scrollbar {
        width: 10px;
      }

      ::-webkit-scrollbar-track {
        background: #f1f1f1;
      }

      ::-webkit-scrollbar-thumb {
        background: #888;
      }

      ::-webkit-scrollbar-thumb:hover {
        background: #555;
      }
  }

  &__info {
    display: block;
    width: 100%;
    max-width: 412px;
  }

  &__info--first {
    color: $info-text-color;
    padding-top: 8px;

    margin: 0;
  }

  &__info--second {
    color: $info-text-numbers;
    margin-bottom: 24px;

    @include hover(transform, scale(1.05));
  }

  &__info--number {
    color: $info-text-numbers;
    text-decoration: none;
  }

  &__info--mail {
    color: $info-text-numbers;
    text-decoration: none;
  }

  &__info--address {
    color: $info-text-numbers;
    text-decoration: none;
  }
}

sources.styles/blockes/button.scss:
.button {
  cursor: pointer;

  @include hover(transform, scale(1.1));

  &__view {
    display: flex;

    width: 324px;
    height: 56px;
    margin: 64px auto 0;
    justify-content: center;
    align-items: center;
    color: $buttom-color;
    background-color: $main-text-color;

    border-radius: 40px;
    border: 3px solid $main-text-color;

    @include hoverActiveEffects;
  }

  &__send {
    display: flex;

    width: 100%;
    height: 56px;
    margin-top: 32px;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    color: $buttom-color;
    background-color: $main-text-color;

    border-radius: 40px;
    border: 3px solid $main-text-color;

    @include hoverActiveEffects;
  }
}
